-- findStore resultType ST0010Dto SELECT SI.STORE_SEQ , SI.COUNT_STORE , SI.STORE_NAME , SI.STORE_ADDRESS , SI.MENU_NAME , SI.REVIEW , MI.FILE_PATH , MI.FILE_ORIGINAL_NAME , MI.FILE_MASKING_NAME , (SELECT NAME_KO FROM public_code pc WHERE 1=1 AND pc.code_category_id = 102 AND pc.CODE = qs.question4) AS question4 , (SELECT NAME_KO FROM public_code pc WHERE 1=1 AND pc.code_category_id = 102 AND pc.CODE = qs.question1) AS question1 , (SELECT NAME_KO FROM public_code pc WHERE 1=1 AND pc.code_category_id = 102 AND pc.CODE = qs.question2) AS question2 , (SELECT NAME_KO FROM publIC_CODE PC WHERE 1=1 AND PC.CODE_CATEGORY_ID = 102 AND PC.CODE = QS.QUESTION3) AS QUESTION3 , (SELECT NAME_KO FROM PUBLIC_CODE PC WHERE 1=1 AND PC.CODE_CATEGORY_ID = 102 AND PC.CODE = QS.QUESTION4) AS QUESTION4 , (SELECT NAME_KO FROM PUBLIC_CODE PC WHERE 1=1 AND PC.CODE_CATEGORY_ID = 102 AND PC.CODE = QS.QUESTION5) AS QUESTION5 , (SELECT NAME_KO FROM PUBLIC_CODE PC WHERE 1=1 AND PC.CODE_CATEGORY_ID = 102 AND PC.CODE = QS.QUESTION6) AS QUESTION6 , (SELECT NAME_KO FROM PUBLIC_CODE PC WHERE 1=1 AND PC.CODE_CATEGORY_ID = 102 AND PC.CODE = QS.QUESTION7) AS QUESTION7 FROM STORE_INFO SI JOIN QUESTION_FOR_SELECT QS ON SI.QUESTION_SEQ = QS.QUESTION_SEQ JOIN MENU_IMG MI ON MI.QUESTION_SEQ = QS.QUESTION_SEQ WHERE 1=1
AND qs.question1 = #{question1}
AND qs.question2 = #{question2}
AND qs.question3 = #{question3}
AND qs.question4 = #{question4}
AND qs.question5 = #{question5}
AND qs.question6 = #{question6}
AND qs.question7 = #{question7}
ORDER BY rand() LIMIT 1
